package basics;

public class Prg3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		
		//program to do addition, subtraction, multiplication, division, and modulus between two numbers.
		
		int a = 10;
		int b = 20;
		
		int add = a+b;
		int sub = a-b;
		int mul = a*b;
		float div = a/b;
		int mod = a%b;
		
		System.out.println("Addition: "+add);
		System.out.println("Subraction: "+sub);
		System.out.println("multiplication: "+mul);
		System.out.println("division: "+div);
		System.out.println("modulus: "+ mod);
		
		
		//program to calculate the area of a rectangle
		
		int L = 15;
		int B = 25;
		
		int R_area = L*B;
		System.out.println("Area of the rectangle is "+R_area);
		
		//program to calculate the area of a circle 
		
		float pi = 3.14f;
		float r = 12.5f;
		float C_area = pi*r*r;
		System.out.println("Area of the circle is "+C_area);
		
		
		
		
		
		
		
		
		
		
		
	
		
	}

}
